#include<iostream>#include<climits>using namespace std; int main(){    int t;    cin>>t;     int p =0;    int maxi = INT_MIN;    while(t--){        int n,m;        cin>>n>>m;        if(n==0) {            p = p + m;            maxi = max(maxi,p);        }        if(n!=0 && m!=0) {            p = p - n;            p = p + m;            maxi = max(maxi,p);         }        if(m==0){             p = p - n;            maxi = max(maxi,p);        }    }    cout<<maxi;    return 0;}