#include<iostream>#include<string.h>using namespace std;bool vowel(char c){    string s = "aeiouyAEIOUY";    for(auto ch: s){         if(ch==c) return true;    }    return false;}int main(){    string s,res="";    cin>>s;    for(auto d: s){        if(vowel(d)){                continue;        }        else        {            res.push_back('.');            if(d>='A' && d<='Z'){                int i = d-'A';                res.push_back('a' + i);            }            else res.push_back(d);        }    }    cout<<res;    return 0;}