#include<iostream>#include<vector> using namespace std; int main(){     int t;    cin>>t;    while(t--){        int n;        int m;        cin>>n>>m;        vector<string>v(n);        for(int i=0; i<n; i++){            cin>>v[i];        }        int res=0;        for(int i=0; i<m; i++){            if(v[n-1][i]=='D') res += 1;        }        for(int i=0; i<n; i++){            if(v[i][m-1]=='R') res += 1;        }        cout<<res<<endl;    }        return 0;}